// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css'; // Import CSS for styling

function ProductList() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedImages, setSelectedImages] = useState({});

  useEffect(() => {
    axios.get('https://dummyjson.com/products')
      .then(response => {
        setProducts(response.data.products || []);
        // Initialize selectedImages object with first image index for each product
        const initialSelectedImages = {};
        response.data.products.forEach(product => {
          initialSelectedImages[product.id] = 0;
        });
        setSelectedImages(initialSelectedImages);
      })
      .catch(error => {
        console.error('Error fetching products: ', error);
      });
  }, []);

  const filteredProducts = products.filter(product =>
    (product.name && product.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (product.category && product.category.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleImageChange = (productId, direction) => {
    const productIndex = filteredProducts.findIndex(product => product.id === productId);
    const maxIndex = filteredProducts[productIndex].images.length - 1;
    let newIndex = selectedImages[productId] + direction;
    if (newIndex < 0) {
      newIndex = maxIndex;
    } else if (newIndex > maxIndex) {
      newIndex = 0;
    }
    setSelectedImages(prevState => ({
      ...prevState,
      [productId]: newIndex
    }));
  };

  return (
    <div>
      <h2>Products</h2>
      <input
        type="text"
        placeholder="Search by name or category"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />
      <div className="product-container">
        {filteredProducts.map(product => (
          <div key={product.id} className="product-item">
            <img
              src={product.images[selectedImages[product.id]]}
              alt={product.name}
              className="product-image"
            />
            <div className="product-description">
              <p>{product.name}</p>
              <p>{product.category}</p>
              <p>${product.price}</p>
            </div>
            <div className="image-navigation">
              <button onClick={() => handleImageChange(product.id, -1)}>Previous</button>
              <button onClick={() => handleImageChange(product.id, 1)}>Next</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
