// App.js
import React, { useState } from 'react';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import cartIcon from './cart.png'; // Import cart icon image

function App() {
  const [isCartOpen, setIsCartOpen] = useState(false); // State to manage cart open/close

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  return (
    <div className="App">
      <div className="header">
        <h1 className="title">InnoCommerce</h1>
        <button className="cart-button" onClick={toggleCart}>
          <img src={cartIcon} alt="Cart" /> {/* Use cart icon image */}
        </button>
      </div>
      <ProductList />
      {isCartOpen && <Cart toggleCart={toggleCart} />}
    </div>
  );
}

export default App;
