// Cart.js
import React from 'react';

function Cart({ cart }) {
  return (
    <div>
      <h2>Cart</h2>
      {cart && cart.length > 0 ? (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              <img src={item.images[0]} alt={item.name} />
              <div>
                <p>{item.name}</p>
                <p>Price: ${item.price}</p>
                {item.discount && <p>Discount: {item.discount}%</p>}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
}

export default Cart;
