// Cart.js
import React from 'react';

function ShoppingCart({ cart }) {
  return (
    <div>
      <h2>Cart</h2>
      <ul>
        {cart.map((item, index) => (
          <li key={index}>
            <img src={item.images[0]} alt={item.title} />
            <div>
              <p>{item.title}</p>
              <p>Price: ${item.price}</p>
              {item.discount && <p>Discount: {item.discount}%</p>}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ShoppingCart;
