// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css'; // Import CSS for styling
import previousArrow from './previous-arrow.png'; // Import arrow icons
import nextArrow from './next-arrow.png'; // Import arrow icons
import Cart from './Cart'; // Import Cart component
import cartIcon from './cart.png'; // Import cart icon image
import AddedToCartMessage from './AddedToCartMessage'; // Import AddedToCartMessage component


function ProductList() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedImages, setSelectedImages] = useState({});
  const [cart, setCart] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false); // State to manage cart visibility

  useEffect(() => {
    axios.get('https://dummyjson.com/products')
      .then(response => {
        setProducts(response.data.products || []);
        // Initialize selectedImages object with first image index for each product
        const initialSelectedImages = {};
        response.data.products.forEach(product => {
          initialSelectedImages[product.id] = 0;
        });
        setSelectedImages(initialSelectedImages);
      })
      .catch(error => {
        console.error('Error fetching products: ', error);
      });
  }, []);

  const filteredProducts = products.filter(product =>
    (product.title && product.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (product.category && product.category.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleImageChange = (productId, direction) => {
    const productIndex = filteredProducts.findIndex(product => product.id === productId);
    const maxIndex = filteredProducts[productIndex].images.length - 1;
    let newIndex = selectedImages[productId] + direction;
    if (newIndex < 0) {
      newIndex = maxIndex;
    } else if (newIndex > maxIndex) {
      newIndex = 0;
    }
    setSelectedImages(prevState => ({
      ...prevState,
      [productId]: newIndex
    }));
  };

  const addToCart = (product) => {
    const existingItemIndex = cart.findIndex(item => item.id === product.id);
    if (existingItemIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[existingItemIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      setCart(prevCart => [...prevCart, { ...product, quantity: 1 }]);
    }
    setIsAddedToCartVisible(true);
    setTimeout(() => {
      setIsAddedToCartVisible(false);
    }, 2000); // Hide message after 2 seconds

  };

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
    if (!isCartOpen) {
      document.body.classList.add('disable-scroll');
    } else {
      document.body.classList.remove('disable-scroll');
    }
  };

  return (
    <div className="product-list-container">
      <div className="header">
        <h1 className="title">InnoCommerce</h1>
        <input
          type="text"
          className="search-bar"
          placeholder="Search by name or category"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        <button className="cart-button" onClick={toggleCart}>
          <img src={cartIcon} alt="Cart" style={{ width: '60px', height: '50px' }} /> {/* Adjust width and height */}
        </button>
      </div>
      <div className={`cart-container ${isCartOpen ? 'open' : ''}`}>
        <Cart cart={cart} setCart={setCart} toggleCart={toggleCart} />
      </div>
      <div className="product-container">
        {filteredProducts.map(product => (
          <div
            key={product.id}
            className="product-item"
            onClick={() => addToCart(product)}
            onMouseEnter={() => setSelectedImages(prevState => ({
              ...prevState,
              [product.id]: 1 // Change the image index to highlight on hover
            }))}
            onMouseLeave={() => setSelectedImages(prevState => ({
              ...prevState,
              [product.id]: 0 // Reset the image index after hover
            }))}
          >
            <div className="product-content">
              <img
                src={product.images[selectedImages[product.id]]}
                alt={product.name}
                className="product-image"
              />
              <div className="product-description" style={{ color: selectedImages[product.id] ? 'blue' : 'black' }}>
                <p>{product.brand}</p>
                <p>{product.title}</p>
                <p>{product.description}</p>
                <p><span className="discount-container">-{product.discountPercentage}%</span> ${product.price}</p>
                <p>Rating: {product.rating}   Stock: {product.stock}</p>
              </div>
            </div>
            <div className="image-navigation">
              <button className="arrow-button" onClick={(e) => { e.stopPropagation(); handleImageChange(product.id, -1) }}>
                <img src={previousArrow} alt="Previous" />
              </button>
              <button className="arrow-button" onClick={(e) => { e.stopPropagation(); handleImageChange(product.id, 1) }}>
                <img src={nextArrow} alt="Next" />
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
