// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css';
import previousArrow from './previous-arrow.png';
import nextArrow from './next-arrow.png';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedImages, setSelectedImages] = useState({});
  const [cart, setCart] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  useEffect(() => {
    axios.get('https://dummyjson.com/products')
      .then(response => {
        setProducts(response.data.products || []);
        const initialSelectedImages = {};
        response.data.products.forEach(product => {
          initialSelectedImages[product.id] = 0;
        });
        setSelectedImages(initialSelectedImages);
      })
      .catch(error => {
        console.error('Error fetching products: ', error);
      });
  }, []);

  const handleImageChange = (productId, direction) => {
    const productIndex = products.findIndex(product => product.id === productId);
    const maxIndex = products[productIndex].images.length - 1;
    let newIndex = selectedImages[productId] + direction;
    if (newIndex < 0) {
      newIndex = maxIndex;
    } else if (newIndex > maxIndex) {
      newIndex = 0;
    }
    setSelectedImages(prevState => ({
      ...prevState,
      [productId]: newIndex
    }));
  };

  const addToCart = (product) => {
    const existingItemIndex = cart.findIndex(item => item.id === product.id);
    if (existingItemIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[existingItemIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      setCart(prevCart => [...prevCart, { ...product, quantity: 1 }]);
    }
  };

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  return (
    <div>
      <h2>Products</h2>
      <input
        type="text"
        placeholder="Search by name or category"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />
      <div className="product-container">
        {products.map(product => (
          <div
            key={product.id}
            className="product-item"
            onClick={() => addToCart(product)}
          >
            <div className="product-content">
              <img
                src={product.images[selectedImages[product.id]]}
                alt={product.name}
                className="product-image"
              />
              <div className="product-description">
                <p>{product.name}</p>
                <p>{product.category}</p>
                <p>${product.price}</p>
              </div>
            </div>
            <div className="image-navigation">
              <button className="arrow-button" onClick={() => handleImageChange(product.id, -1)}>
                <img src={previousArrow} alt="Previous" />
              </button>
              <button className="arrow-button" onClick={() => handleImageChange(product.id, 1)}>
                <img src={nextArrow} alt="Next" />
              </button>
            </div>
          </div>
        ))}
      </div>
      <Cart cart={cart} setCart={setCart} isOpen={isCartOpen} toggleCart={toggleCart} />
    </div>
  );
}

export default ProductList;
